# coding: utf-8
import math


def main(raw_words, raw_scores):
    words = raw_words.split(",")
    words_cleanish = [word[3:-2] for word in words]

    sortedMag = [abs(float(x)) for x in raw_scores.split(",")]

    assert len(words) == len(sortedMag)

    normSortedMag = [math.floor(x/min(sortedMag)) for x in sortedMag]
    repWords = [((words_cleanish[i].lstrip('.').strip()+' ')*normSortedMag[i]).strip() for i in range(len(normSortedMag))]

    print(" ".join(repWords))
    print("https://www.jasondavies.com/wordcloud/")

